import React from 'react'
import {Card,Row,Col,Container} from 'react-bootstrap';
import { FaTrashAlt } from 'react-icons/fa';
import Menu from './Menu';

export default function Favorite() {
    
        var data=JSON.parse(localStorage.getItem("data"));
        function  deleteItem(id) {
            data.filter(el=> el !== el.idMeal);
        }
    return (
        <div className="parent">
            <Container>
            <Menu/>
             <h2 className="titleRes2">Your Favorite Food</h2>
                <Row>
                {
                data.map(el => (
                <React.Fragment key={el.idMeal}>
                    <Col md={4}>
                        <Card m={4} style={{ width: '18rem'}} id="favCard">
                            <Card.Img variant="top" src={el.strMealThumb} id="imgTop"/>
                            <Card.Body>
                                <Card.Title>{el.strMeal}</Card.Title>
                                <Card.Text>
                                 <a href={el.strYoutube} id="lnk">Watch it in youtube!</a>
                                  <FaTrashAlt id="deleteIcon" onClick={deleteItem(el.idMeal)}/>
                                </Card.Text>
                            </Card.Body>
                        </Card>
                    </Col>   
                </React.Fragment>))}
                </Row>
        </Container>
    </div>
    )
}
