import {React,useState,useEffect} from 'react'
import { useParams } from 'react-router-dom';
import {Col,Container,Row,Button,Nav} from 'react-bootstrap';
import axios from 'axios'

export default function Search() {

    let [recipes, setRecipes] = useState([]);
    const {name}=useParams();
    
    useEffect(() => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/search.php?s=${name}`).then(res=>{
            setRecipes(res.data.meals);
        });
    }, [])

    return (
        <div>
        <Container className=' mt-5'>
        <Navbar.Brand className='img'>
              <img src="images/output.png" width="100"  height="90"   className="d-inline-block align-top" alt="Food logo"/>
            </Navbar.Brand>
                    <Nav className="dd  Navbar  mx-ml mr-5">
                        <Nav.Link  className="Navbari" href="#Hero">Home</Nav.Link>
                        <Nav.Link  className="Navbari" href="#About">About</Nav.Link>
                        <Nav.Link  className="Navbari" href="#parent">Menu</Nav.Link>
                        <Nav.Link  className="Navbari" href="#resp">Popular</Nav.Link>
                        <Nav.Link  className="Navbari" href="#favorit" style={{color: "#F98169"}}  ><BsFillHeartFill/></Nav.Link>
                      </Nav>
           {recipes.map(x=> (
             <Row >
               <Col  sm={7} className='d-flex flex-column justify-content-center'>
               <img width={485}height={675}  src={x.strMealThumb}  className="img-fluid"  alt="Food logo"/>
               </Col>

               <Col sm={5} ml={5} className='text d-flex flex-column justify-content-center'>
                    <h2 class="mb-3">{x.strMeal}</h2>
                    <p class="mb-5">
                        {x.strInstructions}
                     <br></br>
                    <Button className='mt-3 btn-get-started '>see more</Button> </p>
                </Col>  
               
               </Row>
            ))}
           
     </Container>

        </div>
    )
}
