import {React,useState,useEffect} from 'react'
import { useParams ,Link} from 'react-router-dom';
import {Row,Container,Button,Nav,Navbar,Col} from 'react-bootstrap';
import axios from 'axios'
import { BsFillHeartFill } from "react-icons/bs";
import { FaRegHeart } from "react-icons/fa";
export default function Search() {

    let [recipes, setRecipes] = useState(["Not Found"]);
    const {name}=useParams();
    
    useEffect(() => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/search.php?s=${name}`).then(res=>{
            setRecipes(res.data.meals);
        });
    }, [])

    return (
        <div>
        <Container >
        <Navbar className="topMenu">
            <Navbar.Brand className='img'>
            <img src="../images/output.png" width="100"  height="90"   className="d-inline-block align-top" alt="Food logo"/>
            </Navbar.Brand>
            <Nav className="dd  Navbar  mx-ml mr-5 ml">
                        <Link  className="linkF" to="/">Home</Link>
                        <Link  className="linkF" to="/">About</Link>
                        <Link  className="linkF" to="/">Menu</Link>
                        <Link  className="linkF" to="/">Popular</Link>
                        <Link  className="linkF" href="#favorit" style={{color: "#F98169"}}  ><BsFillHeartFill/></Link>
             </Nav>
        </Navbar>
           {recipes.map(x=> (
             <Row >
               <Col  sm={7} className='d-flex flex-column justify-content-center'>
               <img  src={x.strMealThumb}  className="img-fluid imgRes"  alt="Food logo"/>
               </Col>
               <Col sm={5} ml={5} className='text d-flex flex-column justify-content-center'>
                    <h2 class="mb-3">{x.strMeal} &ensp;<FaRegHeart className="icns"/></h2>
                    <p class="mb-5">
                        {x.strInstructions}
                     <br></br>
                    <Button className='mt-3 btn-get-started '>view details</Button> </p>
                </Col>  
               
               </Row>
            ))}
           
     </Container>

        </div>
    )
}
