import {React,useState} from 'react'
import { useParams } from 'react-router';

export default function Details() {

    const {id}=useParams();
    
    useEffect(() => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/lookup.php?i==${id}`).then(res=>{
            setRecipes(res.data.meals);
        });
    }, [])

    return (
        <div>
         <h1>{id}</h1>
        </div>
    )
}
