import {React,useState,useEffect} from 'react'
import { useParams } from 'react-router-dom';
import {Card,Container,Row} from 'react-bootstrap';
import axios from 'axios'

export default function Search() {

    let [recipes, setRecipes] = useState([]);
    const {name}=useParams();
    
    useEffect(() => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/search.php?s=${name}`).then(res=>{
            setRecipes(res.data.meals);
        });
    }, [])

    return (
        <div>
        <Container className=' mt-5'>
            <Row >
           {recipes.map(x=> (
               
               <Col  sm={7} className='d-flex flex-column justify-content-center'>
               <img width={485}height={675}  src={x.strMealThumb}  className="img-fluid"  alt="Food logo"/>
               </Col>
               
               
            
            ))}
            </Row>
     </Container>

        </div>
    )
}
