import React, { Component } from 'react'

export default class List extends Component {

    state={
        item:'',
        items:["","PHP/MYSQL","C#/SQLSERVER","JAVA/ORACLE","NodeJs/MongoDB"]
    }
    
    getValue=(e)=>{
        e.preventDefault();
        this.setState({item:e.target.value});
    }
    removeItem=(e)=>{
        e.preventDefault();
       this.setState({items:this.state.items.filter(i => i !== this.state.item)});
    
    }
    render() {
        return (
            <div>
                <form onSubmit={this.removeItem}>
                <select onChange={this.getValue}>
                    {
                        this.state.items.map((item,index)=>{return <option key={index}>{item}</option>;})
                    }
                </select>
                <button>Remove</button>
                </form>
            </div>
        )
    }
}
