import React, { Component } from 'react'
import { FaTrashAlt } from "react-icons/fa";
export default class Form2 extends Component {
      
    constructor(props){
        super(props);
        this.element=React.createRef();
    }

    state={
        items:["PHP/MYSQL","NODEJS/MONGODB","C#/SQLSERVER","JAVA/ORACLE"],
        item:''
    }
    tarik=(e)=>{
      this.setState({item:e.target.value});
    }
    removeItem=(e)=>{
        e.preventDefault();
        this.setState({items:this.state.items.filter(el=> el!== this.state.item)});
    }
    render() {
        return (
            <div>
                 <form>
                     <select onChange={this.tarik}>
                         {this.state.items.map((el,i)=>{return <option key={i}>{el}</option> ; })}
                     </select>

                     <ul>
                     {this.state.items.map((el,i)=>{return <li key={i} ref={this.element}>{el}<span onClick={this.removeItem}><FaTrashAlt/></span></li> ; })}
                     </ul>
                     <button type="submit">Remove</button>
                 </form>
            </div>
        )
    }
}
