import React, { Component } from 'react'

export default class Form extends Component {

    constructor(props){
        super(props);
        this.nom=React.createRef();
        this.age=React.createRef();
    }
   
    afficher=(e)=>{
        e.preventDefault();
        const nom = this.nom.current.value;
        const age = this.age.current.value;
        if(nom==="" || age===""){
            return {__html:'ERROR!!!! il faut saisir tout les champs'};
        }
        else
        return {__html:'hello' + nom +'Vous Avez' + age};
       // document.write("Hello "+nom+" vous avez "+age); 
        //console.log(this.nom);
    }

    render() {
        return (
            <div>
                <form>
                <input placeholder="nom" ref={this.nom}/><br/>
                <input placeholder="Age" ref={this.age}/><br/>
                <h1 dangerouslySetInnerHTML={this.afficher}></h1>
                <button onClick={this.afficher}>Afficher</button>
                </form>
            </div>
        )
    }
}
