    const {id}=useParams();
    let [recipe,setRecipe]=useState();
    useEffect(() => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=£${id}`).then(res=>{
           setRecipe(res.data.meals);
           console.log(recipe);
        });
    }, [])

