import {React,useState,useEffect} from 'react'
import { useParams } from 'react-router-dom';
import {Col,Container,Row,Button,Nav,Navbar,NavDropdown} from 'react-bootstrap';
import axios from 'axios'
import { BsFillHeartFill } from "react-icons/bs";
export default function Search() {

    let [recipes, setRecipes] = useState([]);
    const {name}=useParams();
    
    useEffect(() => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/search.php?s=${name}`).then(res=>{
            setRecipes(res.data.meals);
        });
    }, [])

    return (
        <div>
        <Container className=' mt-5'>
        <Navbar bg="light" expand="lg">
  <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
  <Navbar.Toggle aria-controls="basic-navbar-nav" />
  <Navbar.Collapse id="basic-navbar-nav">
    <Nav className="mr-auto">
      <Nav.Link href="#home">Home</Nav.Link>
      <Nav.Link href="#link">Link</Nav.Link>
      <NavDropdown title="Dropdown" id="basic-nav-dropdown">
        <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
        <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
        <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
        <NavDropdown.Divider />
        <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
      </NavDropdown>
    </Nav>
  
  </Navbar.Collapse>
</Navbar>
           {recipes.map(x=> (
             <Row >
               <Col  sm={7} className='d-flex flex-column justify-content-center'>
               <img width={485}height={500}  src={x.strMealThumb}  className="img-fluid"  alt="Food logo"/>
               </Col>

               <Col sm={5} ml={5} className='text d-flex flex-column justify-content-center'>
                    <h2 class="mb-3">{x.strMeal}</h2>
                    <p class="mb-5">
                        {x.strInstructions}
                     <br></br>
                    <Button className='mt-3 btn-get-started '>see more</Button> </p>
                </Col>  
               
               </Row>
            ))}
           
     </Container>

        </div>
    )
}
