import {React,useState,useEffect} from 'react'
import { useParams } from 'react-router-dom';
import {Row,Container,Button,Nav,Navbar,Col} from 'react-bootstrap';
import axios from 'axios'
import { BsFillHeartFill } from "react-icons/bs";
export default function Search() {

    let [recipes, setRecipes] = useState([]);
    const {name}=useParams();
    
    useEffect(() => {
        axios.get(`https://www.themealdb.com/api/json/v1/1/search.php?s=${name}`).then(res=>{
            setRecipes(res.data.meals);
        });
    }, [])

    return (
        <div>
        <Container className=' mt-5'>
        <Navbar>
            <Navbar.Brand>
            <img src="images/output.png" width="100"  height="90"   className="d-inline-block align-top" alt="Food logo"/>
            </Navbar.Brand>
            <Navbar.Toggle />
            <Navbar.Collapse className="justify-content-end">
                <Navbar.Text>
                Signed in as: <a href="#login">Mark Otto</a>
                </Navbar.Text>
            </Navbar.Collapse>
            </Navbar>
           {recipes.map(x=> (
             <Row >
               <Col  sm={7} className='d-flex flex-column justify-content-center'>
               <img width={485}height={500}  src={x.strMealThumb}  className="img-fluid"  alt="Food logo"/>
               </Col>
               <Col sm={5} ml={5} className='text d-flex flex-column justify-content-center'>
                    <h2 class="mb-3">{x.strMeal}</h2>
                    <p class="mb-5">
                        {x.strInstructions}
                     <br></br>
                    <Button className='mt-3 btn-get-started '>see more</Button> </p>
                </Col>  
               
               </Row>
            ))}
           
     </Container>

        </div>
    )
}
